mapper.update:

update new_repository set 
	is_publish=#{isPublish},
	publish_time=#{publishTime}
	where new_id in 
	
		#{newId}

update new_repository set 
	is_del=#{isDel} 
	where new_id=#{newId};

mapper.select:
select new_id, new_caption, new_sub_title newSubTitle, UNIX_TIMESTAMP(publish_time) publish_time, new_source, new_athors, new_key_word, new_editor, new_type, is_wheel, wheel_img, new_list_img1, new_list_img2, new_list_img3, new_html_file_name, is_publish, show_index, is_show_big_img, UNIX_TIMESTAMP(create_time) create_time, create_user from new_repository t where t.is_del=0
AND t.new_caption like CONCAT(CONCAT('%', #{newCaption, jdbcType=VARCHAR}),'%')
and t.is_publish=1 order by create_time desc LIMIT #{pageNo},#{pageSize}
